package problems.hackerrank.interviewKit;

import java.util.Arrays;

// https://www.hackerrank.com/challenges/common-child/
public class LargestSubsequence {
    static int max = 0;
    static int findSubsequence(char[] strA, char[] strB, int indxA, int indxB, int[][] table){
        if(indxA==strA.length || indxB==strB.length)
            return 0;

        if(table[indxA][indxB]!=-1)
            return table[indxA][indxB];

        if(strA[indxA]!=strB[indxB])
            table[indxA][indxB] = Math.max(findSubsequence(strA,strB,indxA,indxB+1, table),
                    findSubsequence(strA,strB,indxA+1,indxB,table));
        else
            table[indxA][indxB] = Math.max(1+findSubsequence(strA,strB,indxA+1,indxB+1, table),
                    findSubsequence(strA,strB,indxA,indxB+1, table));

        if(table[indxA][indxB]>max)
            max = table[indxA][indxB];

        return table[indxA][indxB];
    }

    static int memoization(String s1, String s2){
        max = 0;
        int[][] table = new int[s1.length()][s2.length()];

        for(int i = 0, n = s1.length(); i<n; ++i){
            for(int j = 0, m = s2.length(); j<m; ++j){
                table[i][j] = -1;
            }
        }

        findSubsequence(s1.toCharArray(), s2.toCharArray(), 0, 0, table);

        return max;
    }

    static int tabulation(String s1, String s2){
        int[][] table = new int[s1.length()+1][s2.length()+1];

        // Initialize table
        for(int i = 1, n = s1.length(); i<=n; ++i){
            for(int j = 1, m = s2.length(); j<=n; ++j){
                if(s1.charAt(i-1)==s2.charAt(j-1)){
                    table[i][j] = Math.max(1+table[i-1][j-1], table[i-1][j]);
                } else {
                    table[i][j] = Math.max(table[i-1][j], table[i][j-1]);
                }
            }
        }

        return table[s1.length()][s2.length()];
    }
    public static void main(String[] args) {
        System.out.println("memoization : " + memoization("ABCE","AABCDEF"));
        System.out.println("tabulation : " + tabulation("ABCE","AABCDEF"));

        System.out.println("memoization : " + memoization("HARRY", "SALLY"));
        System.out.println("tabulation : " + tabulation("HARRY", "SALLY"));

        System.out.println("memoization : " + memoization("SHINCHAN", "NOHARAAA"));
        System.out.println("tabulation : " + tabulation("SHINCHAN", "NOHARAAA"));

        String A = "";
        String B = "";
        System.out.println("memoization : " + memoization(A,B));
        System.out.println("tabulation : " + tabulation(A,B));
    }
}
